mapper.insert:
INSERT INTO
	tb_member_main(
		LOGIN_EMAIL,
		LOGIN_PW,
		NICK_NAME,
		GENDER
	)
VALUE (
	#{member.loginEmail},
	#{member.loginPw},
	#{member.nickName},
	#{member.gender}
);

mapper.update:
UPDATE
	tb_member_main
SET
	LOGIN_PW = #{member.loginPw}
WHERE
	MEMBER_ID = #{memberId};

UPDATE
	tb_member_main
SET
	NICK_NAME = #{member.nickName}
WHERE
	MEMBER_ID = #{memberId};
